import { useState } from "react";
import "./App.css";

const initialFriends = [
  {
    id: 118836,
    name: "Toji Zenin",
    image:
      "data:image/jpeg;base64,",
    balance: -7,
  },
  {
    id: 933372,
    name: "Satoru Gojo",
    image:
      "data:image/jpeg;base64,",
    balance: 20,
  },
  {
    id: 499476,
    name: "Itadori God",
    image:
      "data:image/jpeg;base64,",
    balance: 0,
  },
];

function App() {
  const [showAddFriend, setShowAddFriend] = useState(false);
  const [friends, setFriends] = useState(initialFriends);
  const [splitId, setSplitId] = useState(null);

  function handleSplitBill(
    e,
    id,
    bill,
    yourExpense,
    whoPays,
    setBill,
    setExpense,
    setWhoPays,
  ) {
    e.preventDefault();
    if (id !== null && bill !== 0 && yourExpense !== 0) {
      const friendExpense = Number(bill) - Number(yourExpense);
      const currentFriend = friends.find((item) => item.id === id);
      const currentBalance =
        whoPays === "you"
          ? currentFriend.balance + friendExpense
          : currentFriend.balance - friendExpense;
      setFriends((arr) => arr.filter((item) => item.id !== id));
      setFriends((arr) => [
        ...arr,
        {
          id: id,
          name: currentFriend.name,
          image: currentFriend.image,
          balance: currentBalance,
        },
      ]);
      setBill(0);
      setExpense(0);
      setWhoPays("you");
      setSplitId(null);
    } else {
      e.stopPropagation();
    }
  }

  function handleSelectFriend(id) {
    setSplitId(id);
  }

  function handleShowAddFriend() {
    setShowAddFriend((s) => !s);
  }

  function handleAddFriend(e, item) {
    e.preventDefault();
    setFriends((arr) => [...arr, item]);
    handleShowAddFriend();
  }

  function FriendsList({ onSelect }) {
    return (
      <ul
        style={{
          display: "flex",
          flexDirection: "column",
          alignContent: "space-between",
        }}
      >
        {friends.map((item) => {
          return (
            <Friend
              key={item.id}
              id={item.id}
              profileImg={item.image}
              name={item.name}
              balance={item.balance}
              onSelect={onSelect}
            />
          );
        })}
      </ul>
    );
  }
  function Friend({ profileImg, name, balance, onSelect, id }) {
    return (
      <li style={id === splitId ? { backgroundColor: "#ffe8cc" } : {}}>
        <img src={profileImg} />
        <h3>{name}</h3>
        {balance > 0 && (
          <p className="green">
            {name} owes you ${balance}
          </p>
        )}
        {balance < 0 && <p className="red">You owe ${Math.abs(balance)}</p>}
        {balance === 0 && <p>You and {name} are even</p>}
        {splitId !== id ? (
          <Button onClick={() => onSelect(id)}>Select</Button>
        ) : (
          <Button onClick={() => setSplitId(null)}>Close</Button>
        )}
      </li>
    );
  }

  function Button({ children, onClick }) {
    return (
      <button onClick={onClick} className="button">
        {children}
      </button>
    );
  }

  function AddFriendForm({ onSubmit }) {
    const [item, setItem] = useState({ name: "", image: "", id: 0 });
    let id = new Date().getTime();
    return (
      <form
        className="form-add-friend"
        onSubmit={(e) => onSubmit(e, { ...item, id: id, balance: 0 })}
      >
        <label>Friend name</label>
        <input
          type="text"
          value={item.name}
          onChange={(e) => setItem((i) => ({ ...i, name: e.target.value }))}
        />
        <label>Image Url</label>
        <input
          type="text"
          value={item.image}
          onChange={(e) => setItem((i) => ({ ...i, image: e.target.value }))}
        />
        <Button>Add</Button>
      </form>
    );
  }

  function SplitBillForm({ onSubmit, id }) {
    const [bill, setBill] = useState(0);
    const [yourExpense, setYourExpense] = useState(0);
    const [whoPays, setWhoPays] = useState("you");
    const currentFriend = friends.find((item) => item.id === id);
    return (
      <form
        className="form-split-bill"
        onSubmit={(e) =>
          onSubmit(
            e,
            id,
            bill,
            yourExpense,
            whoPays,
            setBill,
            setYourExpense,
            setWhoPays,
          )
        }
      >
        <h2>Split a bill with</h2>
        <label>Bill value</label>
        <input
          type="text"
          value={bill}
          onChange={(e) => setBill(Number(e.target.value))}
        />
        <label>Your expense</label>
        <input
          type="text"
          value={yourExpense}
          onChange={(e) => setYourExpense(Number(e.target.value))}
        />
        <label>Who pays?</label>
        <select value={whoPays} onChange={(e) => setWhoPays(e.target.value)}>
          <option value="you">You</option>
          <option value="friend">Friend</option>
        </select>
        <Button>Split</Button>
        {id !== null ? (
          <div className="sidebar">
            <li>
              <img src={currentFriend.image} />
              <h3>{currentFriend.name}</h3>
              <p>Spended {bill - yourExpense}</p>
            </li>
          </div>
        ) : (
          <></>
        )}
      </form>
    );
  }

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "row",
      }}
    >
      <div className="sidebar">
        <FriendsList onSelect={handleSelectFriend} />
        {showAddFriend && <AddFriendForm onSubmit={handleAddFriend} />}
        <Button onClick={handleShowAddFriend}>Add Friend</Button>
      </div>
      <div>
        {splitId !== null ? (
          <SplitBillForm onSubmit={handleSplitBill} id={splitId} />
        ) : (
          <></>
        )}
      </div>
    </div>
  );
}

export default App;
